package com.study2;

public class javaMathFunction {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
		
		
		System.out.println(Math.pow(5,3)); // power
		
		System.out.println(Math.sqrt(25));  // square root
		
		
		System.out.println(Math.ceil(5.3)); // always round double number up
		
		System.out.println(Math.floor(5.8)); // always double number down
		
		System.out.println(Math.abs(-35.6)); // absolute value so does not matter positive or negative
		
		
	}

}
